import React from 'react';
import Business from '../Business/Business';
import './BusinessList.css'; // import the BusinessList.css file into my BusinessJist component

class  BusinessList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          businesses: [] // Or some initial array of businesses
        };
      }

    render(){

        return (

            <div className="BusinessList">

            { // Use the map function to iterate over each business in the businesses prop.
            this.props.businesses.map(business => (
                  // For each business in the array, a Business component is rendered.
            <Business key={business.id} // The key prop helps React track which items have changed. It should be a unique value, so business.id is used. 
                      business={business} // The business prop is set to the current business object. This passes the business details to the Business component.
            />
             ))}
             </div>
       );


    }

}

export default BusinessList; // making the BusinessList component available for import in other JavaScript or React files.
